import React, { useState, useLayoutEffect } from "react";
import alanBtn from "@alan-ai/alan-sdk-web";

const Extra = () => {
  const [popularPeople, setPopularPeople] = useState([]);
  const [cart, setCart] = useState([]);

  useLayoutEffect(() => {
    function updateScreen(time) {
      // Make visual updates here.
      alanBtn({
        key: "fa0c4eb284e08b9fd429ea3207df54562e956eca572e1d8b807a3e2338fdd0dc/stage",
        onCommand: (commandData) => {
          console.log(commandData.data);
          if (commandData.command === "getPeople") {
            setPopularPeople(commandData.data);
          }
        },
      });
    }

    requestAnimationFrame(updateScreen);
  }, []);

  //   useEffect(() => {
  //     alanBtn({
  //       key: "fa0c4eb284e08b9fd429ea3207df54562e956eca572e1d8b807a3e2338fdd0dc/stage",
  //       onCommand: (commandData) => {
  //         console.log(commandData.data);
  //         if (commandData.command === "getPeople") {
  //           setPopularPeople(commandData.data);
  //         }
  //       },
  //     });
  //   }, [setPopularPeople]);

  return (
    <div className="App">
      <h1 className="">Popular People</h1>
      {popularPeople.map((person) => (
        <div className="profile-about bg-light p-2" key={person.name}>
          <div className="profile-top my-1 large text-primary">
            {person.name}
          </div>
          <div className="line" />
          <div className="profile-exp p-2">Designation: {person.category}</div>
          <div className="profile-exp p-2">Followers: {person.followers}</div>
        </div>
      ))}
      <h1>Cart:</h1>
      {cart.map((cartItem) => (
        <div key={cartItem.name}>
          {cartItem.name} - {cartItem.followers} - {cartItem.category}
        </div>
      ))}
    </div>
  );
};

export default Extra;
